mapper.insert:
insert into employment (id, student_id, is_sign, 
  company, phone_number, remarks,state,registration_to
  )
values (#{id,jdbcType=INTEGER}, #{studentId,jdbcType=INTEGER}, #{isSign,jdbcType=BIT}, 
  #{company,jdbcType=VARCHAR}, #{phoneNumber,jdbcType=VARCHAR}, #{remarks,jdbcType=VARCHAR},
  #{state},#{registrationTo}
  )

insert into employment (id, student_id, is_sign,
company, phone_number, remarks,state,registration_to
)
VALUES

    (#{item.id,jdbcType=INTEGER}, #{item.studentId,jdbcType=INTEGER}, #{item.isSign,jdbcType=BIT},
    #{item.company,jdbcType=VARCHAR}, #{item.phoneNumber,jdbcType=VARCHAR}, #{item.remarks,jdbcType=VARCHAR},
    #{item.state},#{item.registrationTo}
    )

mapper.select:
select e.id as id, e.student_id as studentId,e.is_sign as isSign,e.company as company,
e.phone_number as phoneNumber,e.remarks as remarks,e.state as state,e.registration_to as registrationTo,
s.name as name,s.number as number,s.department_id as departmentId ,s.major_id as majorId,s.grade,
s.clazz_id as clazzId
from employment e left outer join student s on s.id = e.student_id


        and s.department_id=#{departmentId}



        and s.major_id=#{majorId}



        and s.clazz_id=#{clazzId}



        and s.grade=#{grade}



        and s.abbr_name like CONCAT('%',#{abbrName},'%')


        and s.number like CONCAT('%',#{number},'%')


        and s.name like CONCAT('%',#{name},'%')





    limit #{start},#{pageSize}

select count(*) from employment e join student s on s.id = e.student_id


        and department_id=#{departmentId}



        and major_id=#{majorId}



        and clazz_id=#{clazzId}



        and grade=#{grade}



        and s.abbr_name like CONCAT('%',#{abbrName},'%')


        and s.number like CONCAT('%',#{number},'%')


        and s.name like CONCAT('%',#{name},'%')

select s.name as name,s.number as number,s.department_id as departmentId ,s.major_id as majorId,s.grade as
grade,
s.clazz_id as clazzId,e.company as company, e.phone_number as phoneNumber,e.state as state,e.registration_to as
registrationTo
from employment e left outer join student s on s.id = e.student_id


        and s.department_id=#{departmentId}



        and s.major_id=#{majorId}



        and s.clazz_id=#{clazzId}



        and s.grade=#{grade}



        and s.abbr_name like CONCAT('%',#{abbrName},'%')


        and s.number like CONCAT('%',#{number},'%')


        and s.name like CONCAT('%',#{name},'%')
