mapper.select:
select * from student

    isDel =0

        and department_id=#{departmentId}



        and major_id=#{majorId}



        and clazz_id=#{clazzId}



        and grade=#{grade}


        and name like CONCAT('%',#{search},'%')


        and abbr_name like CONCAT('%',#{abbrName},'%')


        or number=#{search}



    limit #{start},#{end}

select count(*) from student

    isDel =0

        and department_id=#{departmentId}



        and major_id=#{majorId}



        and clazz_id=#{clazzId}



        and grade=#{grade}


        and name like CONCAT('%',#{search},'%')


        and abbr_name like CONCAT('%',#{abbrName},'%')


        or number=#{search}
